from flask import Flask, url_for, request, render_template, redirect
app = Flask(__name__)
@app.route('/admin')
def adminPage():
    return render_template('admin.html')

@app.route('/guest/<name>')
def guest(name):
    return render_template('guest.html', name=name)

@app.route('/gateway/<name>')
def gateway(name):
    if name == 'admin':
        return redirect(url_for('adminPage'))
    else:
        return redirect(url_for('guest', name=name))

@app.route('/voter/<int:age>')
def voter(age):
  return render_template('voter.html', age=age)

@app.route('/products')
def products():
    products = [
        {'name': 'Laptop', 'price': 1000, 'img':'data:image/jpeg;base64,'},
        {'name': 'Mouse', 'price': 20, 'img':'data:image/jpeg;base64,'},
        {'name': 'Keyboard', 'price': 50, 'img':'data:image/jpeg;base64,'},
        {'name': 'Monitor', 'price': 200, 'img':'data:image/jpeg;base64,'},
    ]
    return render_template('products.html', products=products)

@app.route('/formIntro', methods=['POST'])
def formInput():
  if request.method == 'POST':
    name = request.form['name']
    email = request.form['email']
    return render_template('formOutput.html', name=name, email=email)

@app.route('/form')
def form():
    return render_template('forms.html')

if __name__ == '__main__':
    app.run(debug=True)